mapper.update:
update  mainfeed 
set is_deleted = 1
where feed_idx = #{feedIdx};

update  feedlike 
set is_deleted = 1
where feed_idx = #{feedIdx};

update feedcomment 
set is_deleted = 1
where feed_idx = #{feedIdx};

update feedscrap 
set is_deleted = 1
where feed_idx = #{feedIdx};

update mainfeed 
set is_public = #{isPublic}, main_text=#{mainText}, normal_tag=#{normalTag}
where feed_idx=#{feedIdx} and is_deleted!='1'
